        expect(json).not_to be_empty
        expect(json['id']).to eq(company_item.id)
      end

      it "return all company info" do
        expect(json["id"]).to eq(company_item.id)
        expect(json["name"]).to eq(company_item.name)
        expect(json["company_name"]).to eq(company_item.company.company_name)
        expect(json["product_type"]).to eq(company_item.product_type)
        expect(json["description"]).to eq(company_item.description)
        expect(json["price"]).to eq(company_item.price.to_s)
        expect(json["has_image"]).to eq(true)
        expect(json["country"]).to be_a_kind_of(String)
        expect(json["country_id"]).not_to be_present
        # expect(json["link_to_store"]).to be_a_kind_of(String)
        expect(json["shopify_id"]).not_to be_present
        expect(json["tags"]).to eq(company_item.company_item_tags.pluck(:tag))
        expect(json["image"]).not_to be_present
      end

      it 'returns status code 200' do
        expect(response).to have_http_status(200)
      end
    end

    context 'when the record without image' do
